package hybrid;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;

class EncryptionTest {

    private Encryption encryption = new Encryption();

    @Test
    public void encryptionTest() {
        String message = "ASDasdfaSDFasdfasfgASDa\nsdfaSDFasdfasfgASDa\nsdfaSDFasdfas}fgASDasdf{aSDFasdfasfg$ASDasdfaSDF@";
        String encrypted = encryption.encrypt(message);
        assertEquals(message, encryption.decrypt(encrypted));
    }

}